#map companies to industries
df_real["INDUSTRY"]=df_real["NAME"].map(industry_dict)
#collect inputs from streamlit
industry_select=st.selectbox("Select an Industry", ('Technology', 'Healthcare', 'Finance', 'Retail', 'Energy', 'Manufacturing'))
number_companies=st.slider(f"Top # of Companies in: {industry_select}", 1,20,5)#[ST3]

#print top companies per inputs
industry_data = df_real[df_real['INDUSTRY'] == industry_select]
top_revenues=industry_data.sort_values(by="REVENUES", ascending=False).head(number_companies)#[DA3]
st.subheader(f"Top {number_companies} companies in {industry_select} by Revenue")
st.dataframe(top_revenues[["NAME", "REVENUES"]])

#Produce chart based on inputs
st.write("Pie Chart for Top", number_companies, "companies in", industry_select)
plt.pie(top_revenues["REVENUES"], labels=top_revenues["NAME"],autopct='%1.1f%%')
st.pyplot(plt)
plt.clf()

#Produce Chart of all companies within that industry
industry_data['REVENUE_PERCENT']=industry_data['REVENUES'] / (industry_data['REVENUES'].sum())*100#[DA9]
threshold = 2  # Percentage threshold for grouping small slices
industry_data['LABELS'] = industry_data.apply(lambda row: row['NAME'] if row['REVENUE_PERCENT'] > threshold else 'Other <2%', axis=1)#[DA1] #[DA4]
pie_data = industry_data.groupby('LABELS', as_index=False)['REVENUES'].sum()
num_colors = len(pie_data)
colors = sns.color_palette("tab20", num_colors)
st.subheader(f"Pie Chart for all companies in {industry_select}")
plt.pie(pie_data["REVENUES"], labels=pie_data["LABELS"], colors=colors,autopct='%1.1f%%')
plt.legend(title="COMPANIES", loc="upper right",bbox_to_anchor=(1.7, 1))
st.pyplot(plt)

#Compare all the industries to each other
revenue_by_industry=df_real.groupby('INDUSTRY', as_index=False).sum() #[DA7]
